from typing import List

class Solution:
    def findLength(self, nums1: List[int], nums2: List[int]) -> int:
        # Create a list of occurences of each unique element in nums2
        num2_occ = {}
        for idx, num in enumerate(nums2):
            if num in num2_occ:
                num2_occ[num].append(idx)
            else:
                num2_occ[num] = [idx]
        
        max_sub = i = 0
        # Iterate nums1
        while i < len(nums1):
            # If element not in nums2 then no need to analyse
            if nums1[i] in num2_occ:
                curr_max_len = 0
                # Iterate all occurences of nums1[i] in nums2
                for idx in num2_occ[nums1[i]]:
                    j = i
                    k = idx
                    temp_sub_len = 0
                    # go forwards and find subarray elements
                    while j < len(nums1) and k < len(nums2) and nums1[j] == nums2[k]:
                        temp_sub_len += 1
                        j += 1
                        k += 1
                    j = i - 1
                    k = idx - 1
                    # go backwards and find subarray elements
                    while j >= 0 and k >= 0 and nums1[j] == nums2[k]:
                        temp_sub_len += 1
                        j -= 1
                        k -= 1
                    # print(f"obv {i, idx} , -> {temp_sub_len}")
                    curr_max_len = max(curr_max_len, temp_sub_len)
                # print(f"for i {i} max_len {curr_max_len}: subarr: {nums1[j:j+curr_max_len+1]} == {nums2[k:k+curr_max_len+1]}")
                i += curr_max_len # Skip directly to last element of subarray
                max_sub = max(max_sub, curr_max_len)
                continue
            i += 1
        return max_sub


